package org.openmrs.scheduler;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;/** *  * @author jmiranda */public class SchedulableFactory {	/**	 *  Singleton instance of the schedulable factory	 */	private final static SchedulableFactory factory = new SchedulableFactory();		/**	 * Logger 	 */	private static Log log = LogFactory.getLog(SchedulableFactory.class);	  	/**	 * Private constructor 	 */	private SchedulableFactory() { }		/**	 * Gets an instance of the schedulable factory	 * @return	 */	public static SchedulableFactory getInstance() { 		return factory;	}  	/**	 * Creates a new instance of Schedulable used to run tasks. 	 * @param taskConfig	 * @return	 * @throws SchedulerException	 */	public Schedulable createInstance(TaskConfig taskConfig) throws SchedulerException {		try {			Class schedulableClass = Class.forName( taskConfig.getSchedulableClass() );			Schedulable schedulable = (Schedulable) schedulableClass.newInstance();			schedulable.initialize(taskConfig);			return schedulable;		} 		catch (Exception e) { 			log.error("Error creating new task for class " + taskConfig.getSchedulableClass(), e );			throw new SchedulerException(e);		}	}}